INSERT INTO `instructor_items` (`id`, `fio`, `image`, `annotation`, `body`, `clubs`, `tags`, `meta_index`, `meta_title`, `meta_keywords`, `meta_description`, `created_ip`, `created_date`, `created_user`, `created_username`, `modified_ip`, `modified_date`, `modified_user`, `modified_username`, `active`) VALUES
	(3, 'Аверкиева Мария', 'instructor_1395746532.jpg', 'Инструктор групповых направлений', '<p><strong>Образование:</strong> высшее, ЧГУ им. Ульянова, экономический факультет. Сертифицированный инструктор НОУ "Академия традиционных систем оздоровления и развития человека" по направлению "Классическая йога. Хатха йога".</p>\r\n<p><strong>Работа:</strong> с 2013 г. фитнес-клуб "Цитрус", с 2012 г. студия йоги "Практика".</p>\r\n<p><strong>Личные достижения:</strong> участница семинаров "Йога 23, йога 23 fit", "Чакральная система", "йога, психология парных отношений".</p>\r\n<p><strong>Направления работы:</strong> йога, йога для лица, калланетика.</p>', NULL, 'joga,group,averkieva_maria', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1),
	(4, 'Анастасия Полубарьева', 'instructor_1395746792.jpg', 'Инструктор тренажерного зала, групповых направлений, детский фитнес', '<p><strong>Образование:</strong> ЧГПУ им. Яковлева, Сертифицированый инструктор тренажерного зала Сертифицированный инструктор групповых программ; Сертифицированный инструктор детских направлений.</p>\r\n<p><strong>Направления работы:</strong> Body Tonic Pilates, Инструктор тренажерного зала, Инструктор детских направлений</p>', NULL, 'group,joga,anastasia_polubarieva', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1),
	(5, 'Андрей Гладышев', 'instructor_1395746978.jpg', 'Инструктор тренажерного зала', '<p><strong>Образование:</strong> высшее педагогическое. Тренер по Киокушинкай каратэ (коричневый пояс) с 1991 по 1996г. Золотой значок ГТО, Первый юношеский разряд по тяжелой атлетике. Киокушинкай каратэ (коричневый пояс).</p>\r\n<p><strong>Участие в соревнованиях:</strong> по легкой атлетике (кросс). По тяжелой атлетике. По каратэ, кикбоксингу.</p>', NULL, 'trenazherniy,andrey_gladyshev', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1),
	(6, 'Данил Дергачев', 'instructor_1395832748.jpg', 'Инструктор тренажерного зала', '<p><strong>Образование:</strong> высшее, обучение инструкторной деятельности (г.Казань, "Планета фитнес").</p>\r\n<p><strong>Стаж и место работы:</strong> ФК Цитрус 2010-2013, ИКС-ФИТ 2011-2012.</p>\r\n<p><strong>Направление работы:</strong> Повышение мотивации при занятиях фитнесом благодаря индивидуальному сопровождению</p>\r\n<p>Обеспечение высокой эффективности и максимальной пользы тренировок благодаря индивидуальному подходу к тренировочному процессу</p>', NULL, 'trenazherniy,danil-dergachev', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1),
	(7, 'Дмитрий Скворцов', 'instructor_1395832863.jpg', 'Инструктор тренажерного зала', '<p>Имеется медицинское образование,</p>\r\n<p>сертификат "Принципы жиросжигания и гипертрофии" 29.06.2013 Дмитрий Яшанькин.</p>\r\n<p>сертификат "высокоинтенсивный тренинг в тренажёрном зале" WellFitnes.</p>\r\n<p>сертификат "функциональный тренинг в тренажёрном зале" WellFitnes.</p>\r\n<p></p>\r\n<p>1-ый разряд по классическому пауэрлифтингу,</p>\r\n<p>Чемпион Чувашии по классическому пауэрлифтингу,</p>\r\n<p>многократный призёр в силовом троеборье со штангой и классическому жиму лёжа,</p>\r\n<p></p>\r\n<p>Проводит тренировки по фитнесу (анаэробного, высокоинтенсивного, аэробного, кардио, интервального, многофункционального) характера и классическому пауэрлифтингу персонально.</p>', NULL, 'trenazherniy,dmitriy_skvorcov', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1),
	(8, 'Илларионов Денис', '', 'Инструктор тренажерного зала', '<p><strong>Образование:</strong> неоконченное высшее СПбГИЭУ ИЖЭКОН</p>\r\n<p><strong>Работа:</strong> Египет, Шарм-Эль-Шейх 01.06.2011-14.10.2011; Фитнес клуб Мэтро 01.07.2013-01.09.2013;</p>\r\n<p><strong>Направление работы:</strong> Инструктор тренажерного зала, персональный тренер</p>', NULL, 'trenazherniy,illarionov_denis', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1),
	(9, 'Коркин Кирилл', 'instructor_1395833072.jpg', 'Инструктор тренажерного зала', '<p><strong>Образование:</strong> Высшее. &laquo;Уральский федеральный университе имени первого Президента России Б.Н. Ельцина&raquo; (УрФУ) г. Екатеринбург 2006-2013г.г.</p>\r\n<p><strong>Квалификация:</strong> Специалист по физической культуре и спорту по специальности &laquo;Двигательная рекреация в основе фитнеса&raquo;</p>\r\n<p>Сертифицированный инструктор &laquo;Frech Fitness Academy&raquo;</p>\r\n<p>Сертифицированный инструктор по CrossFit &laquo;CrossFit Trainer Level 1&raquo;</p>\r\n<p><strong>Работа:</strong> Фитнес центр "Frech Fitness" Екатеринбург, 2006&ndash;2007 - Персональный тренер "Уральский центр кинезиотерапии" Екатеринбург, 2007&ndash;2012 - инструктор-реабилитолог Фитнес центр "Extreme Fitness Athletics" Екатеринбург, 2007&ndash;2009 - Персональный тренер Wellness-клуб "Тихвинъ" Екатеринбург, 2009&ndash;2013 - Персональный тренер Фитнес центр "Цитрус" Чебоксары, с 2013 г. - Персональный тренер</p>\r\n<p><strong>Личные достижения:</strong> КМС (Кандидат в мастера спорта) по мини футболу. Многократный чемпион России по мини-футболу. Многократный призер чемпионатов по CrossFit.</p>\r\n<p><strong>Направления работы:</strong> CrossFit Силовые тренировки, Функциональный тренинг, Кинезиотерапия (реабилитация)</p>', NULL, 'trenazherniy,korkin_kirill', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1),
	(10, 'Ласковенков Александр', 'instructor_1395833163.jpg', 'Инструктор по йоге', '<p>Сертификационный треннинг FPA (Ассоциация профессионалов фитнеса). &laquo;Инструктор тренажерного зала&raquo;.</p>\r\n<p>Сертификат Академии фитнеса &laquo;Персонального тренера тренажерного зала&raquo;.</p>\r\n<p>Сертификат академии Wellness &ldquo;Инструктор по Хатха-йога&rdquo;.</p>\r\n<p>Сертификационные треннинги по Yoga 23, Хатха - йоге, йоге &mdash; Айенгара.</p>\r\n<p><strong>Работа по направлениям:</strong> Персональные тренировки по йоге, Персональные тренировки в тренажерном зале, Групповые тренировки по йоге, Проведение мастер-классов.</p>', NULL, 'group,joga,laskovenkov_aleksandr', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1),
	(11, 'Морозова Ксения', 'instructor_1395833270.jpg', 'Инструктор групповых и танцевальных направлений, Персональный тренер\r\n', '<p><strong>Образование:</strong> ННГУ им.Лобачевского</p>\r\n<p>Член Федерации Фитнес Аэробики России,</p>\r\n<p>инструктор по класической аэробики, и силовым направлениям;</p>\r\n<p>Сертифицированный тренер по стрип-пластике; Сертифицированный тренер по стретчингу;</p>\r\n<p>Школа восточного танца &laquo;Аиша&raquo; по направлению Афро Джаз;</p>\r\n<p>Сертифицированный тренер по детскому фитнесу;</p>\r\n<p></p>\r\n<p><strong>Работа:</strong> С 2008 года инструктор групповых программ и персональный тренер.</p>\r\n<p><strong>Личные достижения:</strong> Опыт работы тренером 5 лет; Профессиональный танцор (стаж более 15 лет); Постоянный участник международных и региональных конвенций и мастер-классов по фитнесу;</p>\r\n<p><strong>Направления работы:</strong> Аэробика, степ, Стретчинг, Танцевальные, силовые , Функциональные направления</p>', NULL, 'group,dance,morozova_kseniya', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1),
	(12, 'Павел Совин', 'instructor_1395833361.jpg', 'Инструктор тренажерного зала', '<p><strong>Образование:</strong> высшее (ЧГУ им. Ульянова), курсы персонального тренера (академия фитнеса, Казань)</p>\r\n<p><strong>Работа:</strong> ФК Цитрус - с 2011 года</p>\r\n<p><strong>Личные достижения:</strong> 1 взр. Бокс</p>\r\n<p><strong>Направление работы:</strong> коррекция фигуры, снижение веса, улучшение общей физической подготовки клиента.</p>', NULL, 'trenazherniy,pavel_sovin', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1),
	(13, 'Токсубаева Татьяна', 'instructor_1395833459.jpg', 'Инструктор групповых программ, персональный тренер\r\n', '<p>Сертифицированный инструктор образовательного центра &laquo;Академия Фитнеса&raquo; по пост-изометрической релаксации СТРЕТЧИНГ с 2012 г</p>\r\n<p>Сертифицированный инструктор Академии Wellnes, совместно с ассоциацией спортивной аэробики России (АСАР) по Тай-бо и методике проведения групповых занятий с 2011 г.</p>\r\n<p><strong>Работа:</strong> с 2009 г. инструктор групповых программ. С 2013 г. персональный тренер</p>\r\n<p><strong>Направления работы:</strong> Аэробика, Тай-бо, Боди-Фит, Боди-Рок, Стретчинг, Функционал.</p>', NULL, 'group,tatiana_toksubaeva', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1),
	(14, 'Матвеева Дарья', 'instructor_1400253699.jpg', 'Инструктор групповых направлений', '<p>Матвеева Дарья Юрьевна</p>\r\n<p>Инстуктор групповых направлений по&nbsp; Body Ballet и Stretching<br />Персональный тренер&nbsp;</p>\r\n<p><br />В 2007 году она с отличием окончила Музыкальное училище им. Ф. П. Павлова г.Чебоксары, 2004&ndash;2007 по специальности Хореографическое искусство и получила квалификацию артистки балета. Работала в Имперском русском балете, и Марийском государственном театре оперы и балета им.Э.Сапаева солисткой балета. <br />Участвовала в Балетном фестивале &laquo;Только звезды&raquo; (г.Мурманск, 2005г.).</p>\r\n<p>Является Дипломантом Конкурса молодых артистов балета Артека (Украина,2006) и дипломантом за актерское мастерство (за создание сценического образа).</p>\r\n<p>Принимала участие в семинаре "Пост-изометрическая релаксация Stretching" (Академии Фитнеса, 2012)</p>\r\n<p>С 2010 года является инструктором групповых программ и персональным тренером по направлениям Stretching, Body Ballet и Классическая хореография!</p>\r\n<p></p>', '', '', 1, '', '', '', NULL, NULL, NULL, '', NULL, NULL, NULL, '', 1);
