INSERT INTO `club_items` (`id`, `title`, `annotation`, `description`, `body`, `manager_email`, `contact_phones`, `contact_address`, `contact_info`, `contact_coordinates`, `created_ip`, `created_date`, `created_user`, `created_username`, `modified_ip`, `modified_date`, `modified_user`, `modified_username`, `active`) VALUES
(-1, 'Цитрус', '', '', '<p>Сеть фитнес-клубов &laquo;Цитрус&raquo; &mdash; это современные и удобные клубы различных форматов по всей стране. В наших клубах вы можете отложить все свои проблемы и посвятить время только себе и своему телу.</p>\r\n<p>Клубы сети &laquo;Цитрус&raquo; всегда делают упор на качественный подход к тренировкам. Нашим клиентам мы предлагаем персональные занятия с квалифицированными инструкторами, как в тренажерном зале, так и в залах персональных тренировок. Наши специалисты проведут фитнес-тестирование и подберут нужную вам программу питания и тренировок для того, чтобы вы добились поставленного результата. Тренировки проводят сертифицированные специалисты в любое удобное для вас время!</p>\r\n<p>Для вас ежедневно открыты двери тренажерного зала, залов групповых занятий, залов персонального тренинга, маникюрного салона, SPA-кабинета и солярия. Различные танцевальные направления, несколько видов йоги и аэробики, пилатеса и стрейтчинга, посещение тренажерного зала помогут вам находиться в тонусе и хорошем самочувствии всегда!</p>\r\n<p>В каждом клубе сети располагаются удобные сауны в раздевалках, которые снимут стресс и усталость после тренировки.</p>\r\n<p>Огромное значение мы уделяем месторасположению клубов. Все клубы сети &laquo;Цитрус&raquo; располагаются в шаговой доступности от остановок общественного транспорта. Кроме этого к клубам организованы удобные подъездные пути для автомобилистов. Каждый клуб обустроен удобной охраняемой парковкой для автомобилей.</p>\r\n<p>Наши клубы работают по системе клубных карт. Плюсы клубной системы заключаются в возможности посещения различных видов групповых занятий и тренажерного зала без ограничений. С клубной картой вы можете участвовать в проходящих в клубе семинарах. Вы получите скидки и бонусы на пользование дополнительными услугами клуба и партнерских организаций.</p>\r\n<p>Дополнительную информацию о ценах на клубные карты и индивидуальные занятия вы можете узнать в отделах продаж клубов &laquo;Цитрус&raquo; или во вкладке &laquo;Каталог&raquo;. Для корпоративных клиентов действует гибкая система скидок и различные схемы оплаты (подробности: корпоративный фитнес).</p>\r\n<p>Всегда рады видеть вас на занятиях в сети фитнес-клубов &laquo;Цитрус&raquo;!</p>', 'sales@citrusfit.ru', '8 (8352) 49-00-30', '', '', '', NULL, NULL, NULL, '', '5.158.232.222 Mozilla/5.0 (Macintosh; Intel Mac OS X 10_10_0) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/38.0.2125.111 Safari/537.36', '2014-11-07 13:31:13', NULL, '', 0),
(1, 'Фитнес-клуб "Цитрус" СЗР, г. Чебоксары', '<p>{{w:GalleryBlock|album=Клуб СЗР;limit=9;modal=1;order=id desc;width=100}}</p>', '<p style="text-align: justify;">Первый в&nbsp;Чебоксарах фитнес-клуб, работающий по&nbsp;системе клубных карт &laquo;Все включено&raquo;. В&nbsp;клубе есть просторный тренажерный зал, огромное количество групповых направлений, фитнес-бар и финская сауна в&nbsp;каждой раздевалке. Наши квалифицированные инструктора всегда помогут вам добиться поставленных целей.</p>', '<p style="text-align: justify;">Фитнес-клуб &laquo;Цитрус СЗР&raquo;&nbsp;&mdash; уютное место в&nbsp;центре Северо-Западного района г.Чебоксары, где вы&nbsp;можете отложить все свои проблемы и&nbsp;посвятить время только себе и&nbsp;своему телу. Удобные сауны в&nbsp;раздевалках снимут стресс и&nbsp;облегчат усталость после тренировки.</p>\r\n<p style="text-align: justify;">Для вас ежедневно открыты двери тренажерного зала, зала групповых занятий и&nbsp;фитнес-бара. Различные танцевальные направления, несколько видов йоги и&nbsp;аэробики, пилатеса и&nbsp;стрейтчинга, посещение тренажерного зала помогут вам находиться в&nbsp;тонусе и&nbsp;хорошем самочувствии всегда!</p>\r\n<p style="text-align: justify;">Все это в&nbsp;шаговой доступности от&nbsp;остановки общественного транспорта Торговый дом "Европа" с&nbsp;удобной охраняемой парковкой для автомобилей.</p>\r\n<p style="text-align: justify;">Ценителям индивидуального подхода мы&nbsp;предлагаем персональные занятия с&nbsp;инструктором как в&nbsp;тренажерном зале, так и&nbsp;в&nbsp;залах персональных тренировок. Наши специалисты проведут фитнес-тестирование и&nbsp;подберут нужную вам программу питания и&nbsp;тренировок.</p>\r\n<p style="text-align: justify;">Занятия фитнесом для будущих мам возможны в&nbsp;индивидуальном порядке и&nbsp;в&nbsp;коммерческих группах до&nbsp;6&nbsp;человек. Тренировки проводят сертифицированные специалисты в&nbsp;любое удобное для вас время!</p>\r\n<p style="text-align: justify;">Наш клуб работает по&nbsp;системе клубных карт, что является неоспоримым преимуществом в&nbsp;Чебоксарах. Плюсы клубной системы заключаются в&nbsp;возможности посещения различных видов групповых занятий, а&nbsp;также тренажерного зала без ограничений, участии в&nbsp;проходящих в&nbsp;клубе семинарах, в&nbsp;скидках и&nbsp;бонусах на&nbsp;пользование дополнительными услугами клуба.</p>\r\n<p style="text-align: justify;">Дополнительную информацию о&nbsp;ценах на&nbsp;клубные карты и&nbsp;индивидуальные занятия вы можете узнать в&nbsp;отделе продаж клуба &laquo;Цитрус СЗР&raquo; или во&nbsp;вкладке &laquo;<a href="http://citrusfit.ru/catalog/" title="Клубные карты">Каталог</a>&raquo;. Для корпоративных клиентов действует гибкая система скидок и&nbsp;различные схемы оплаты (подробности: <a href="http://citrusfit.ru/korporativhiy_fitnes/" title="Корпоративный фитнес">корпоративный фитнес</a>).</p>\r\n<p style="text-align: justify;">Звоните нам по&nbsp;телефону 49-01-30, и&nbsp;менеджеры клуба ответят на&nbsp;все ваши вопросы. Фитнес-клуб работает с&nbsp;понедельника по&nbsp;пятницу с 7:00 до 23:00, по субботам&nbsp;с&nbsp;9:00 до&nbsp;21:00, по воскресеньям с 10:00 до 19:00. Наш адрес&nbsp;&mdash;&nbsp;<span>ул. Лебедева, 7Б (5 минут пешком от ост. Торговый дом "Европа"), здание бывшего ФЦ "Велайн".</span></p>\r\n<p style="text-align: justify;">Всегда рады видеть вас на&nbsp;занятиях в&nbsp;фитнес-клубе &laquo;Цитрус СЗР&raquo;!</p>', 'sales@citrusfit.ru', '8 (8352) 49-00-30, 49-01-30', 'г. Чебоксары, ул. Лебедева, 7Б', '', '56.14849613, 47.16492150', NULL, NULL, NULL, '', '85.234.7.89 Mozilla/5.0 (Windows NT 6.3; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/49.0.2623.112 Safari/537.36', '2016-04-22 14:06:14', 2, 'manager', 1),
